DROP TABLE items;
CREATE TABLE items (itemid int NOT NULL AUTO_INCREMENT COMMENT '序号', itemname varchar(40) COMMENT '产品名称', brandname varchar(40) COMMENT '品牌', modelnumber varchar(40) COMMENT '型号', specs varchar(40) COMMENT '规格', unitprice decimal(36,6) COMMENT '单价', picture varchar(200) COMMENT '图片', setuppicture varchar(200), remark varchar(200) COMMENT '备注', PRIMARY KEY (itemid)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_general_ci;
DROP TABLE offeritems;
CREATE TABLE offeritems (offeritemid int NOT NULL AUTO_INCREMENT COMMENT '序号', offerid int NOT NULL COMMENT '报价序号', itemid int NOT NULL COMMENT '项目序号', itemname varchar(40) COMMENT '项目名称', brandname varchar(40) COMMENT '品牌', modelnumber varchar(40) COMMENT '型号', specs varchar(40) COMMENT '规格', unitprice decimal(36,6) COMMENT '单价', quotedprice decimal(36,6) COMMENT '报价', picture varchar(200) COMMENT '图片', setuppicture varchar(200), remark varchar(200) COMMENT '备注', PRIMARY KEY (offeritemid)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_general_ci;
DROP TABLE offers;
CREATE TABLE offers (offerid int NOT NULL AUTO_INCREMENT COMMENT '序号', offerno varchar(20) COMMENT '报价编号', inquirer varchar(20) COMMENT '询价人员', amount int COMMENT '数量', contact varchar(20) COMMENT '联系方式', city varchar(20) COMMENT '省份城市', prodid int COMMENT '产品', address varchar(200) COMMENT '联系地址', remark varchar(200) COMMENT '备注', PRIMARY KEY (offerid)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_general_ci;
DROP TABLE packages;
CREATE TABLE packages (packageid int NOT NULL AUTO_INCREMENT COMMENT '序号', prodid int COMMENT '产品名称', itemid int COMMENT '品牌', remark varchar(200) COMMENT '备注', PRIMARY KEY (packageid)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_general_ci;
DROP TABLE products;
CREATE TABLE products (prodid int NOT NULL AUTO_INCREMENT COMMENT '序号', prodname varchar(40) COMMENT '产品名称', remark varchar(200) COMMENT '备注', PRIMARY KEY (prodid)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_general_ci;
